parameters.body/body/messageBody:
{
    "type": "AdaptiveCard",
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "body": [
        {
            "type": "TextBlock",
            "text": "Download Request",
            "wrap": true,
            "weight": "Bolder",
            "size": "Medium",
            "fontType": "Monospace"
        },
        {
            "type": "TextBlock",
            "text": "Assigned to:@{triggerBody()?['Assignedto0']?['DisplayName']}",
            "wrap": true
        },
        {
            "type": "FactSet",
            "facts": [
                {
                    "title": "Acronym",
                    "value":"@{triggerBody()?['Acronymn']}"
                },
                {
                    "title": "Media Format"
                   "value":"@{item()?['Value']}"
                },
                {
                    "title": "Episodes"
                   "value":"@{triggerBody()?['Episodes']}"
                },
                {
                    "title": "Delivery Location"
                     "value":"@{triggerBody()?['DeliveryLocation']?['Value']}"
                },
                {
                    "title": "Date"
                     "value":"@{triggerBody()?['DateReported']}"
                },
                {
                    "title": "AsperaPass-Phrase"
                     "value":"@{triggerBody()?['AsperaPass_x002d_phrase']}"
                },
                {
                    "title": "Credentials"
                     "value":"@{triggerBody()?['Credentials']}"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Details:",
            "wrap": true
        },
        {
            "type": "RichTextBlock",
            "inlines": [
                {
                    "type": "TextRun",
                    "text": "@{triggerBody()?['Status']?['Value']}"
                }
            ]
        },
        {
            "type": "Input.ChoiceSet",
            "choices": [
                  {
                    "title": "Issues with Assets",
                    "value": "Choice 1"
                },
                {
                    "title": "Completed",
                    "value": "Choice 2"
                },
                {
                    "title": "In Progress",
                    "value": "Choice 3"
                }
            ],
            "placeholder": "Status"
        },
        {
            "type": "Input.Text",
            "placeholder": "Enter issue Notes"
        },
        {
            "type": "ActionSet",
            "actions": [
                {
                    "type": "Action.Submit",
                    "title": "Complete"
                }
            ]
        }
    ]
}
